import React, { useEffect, useMemo, useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Home.css';

const sampleImages = [
  '/uploads/Wedding%201.jpg',
  '/uploads/Wedding%202.jpg',
  '/uploads/Lifestyle%201.jpg',
  '/uploads/Portrait%201.jpg',
  '/uploads/Commercial%201.jpg'
];

function shuffle(array) {
  const arr = array.slice();
  for (let i = arr.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [arr[i], arr[j]] = [arr[j], arr[i]];
  }
  return arr;
}

const Home = () => {
  const [order, setOrder] = useState(() => shuffle(sampleImages));
  useEffect(() => {
    const id = setInterval(() => setOrder(shuffle), 5000);
    return () => clearInterval(id);
  }, []);

  const tiles = useMemo(() => order.slice(0, 6), [order]);

  return (
    <section className="home">
      <header className="home-header">
        <h1>Goldswain Lifestyle Photographer</h1>
        <p>Capturing authentic moments & pure emotion.</p>
        <Link to="/gallery" className="cta">View Gallery</Link>
      </header>
      <div className="featured-gallery">
        {tiles.map((src, idx) => (
          <div className="featured-item" key={idx}>
            <div className="featured-img-wrapper">
              <img src={src} alt="Featured" />
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Home;
